import { Button, Card, CardContent, Grid2, TextField, Typography } from "@mui/material"
import { useState, useEffect } from "react";
import { createRequestHandler } from "react-router-dom";

function CrearUsuario() {

  const [valores, setValores] = useState({
    nombre: '',
    correo: '',
    rol: '',
    clave: ''
  })

  const handleSubmit = async (e) =>{
    e.preventDefault();
    const respuesta = await fetch('http://localhost:4000/tareas', {
      method: 'POST',
      body: JSON.stringify(valores), // lo que hacemos es transformas los datos en un string 
      headers: {"Content-Type": "application/json"}, //esto se hace para que sepa que es un objeto json
    }) 
    const data = await respuesta.json();
    console.log(data);
    };

  const handleChange = (e) => 
    setValores({...valores, [e.target.name]: e.target.value});
  

  return (
    <Grid2 container direction="colum" alignItems='center' justifyContent='center'>
      <Grid2 item xs={3}>
        <Card sx={{mt: 5}} style={{
          backgroundColor: '#1e272e',
          padding: "1rem"
        }}>
          <Typography variant="5" textAlign="center" color="white">
            Crear un Usuario
          </Typography>

          <CardContent> {/** par acolocar elementos dentro de la tarjeta */}
            <form onSubmit={handleSubmit}>
              <TextField  
              variant='filled' 
              label="Usuario"
              
              sx={{
                display: "block",
                margin: '.5rem 0'
              }}
              name = 'usuario'
              onChange={handleChange}

              slotProps={{
                htmlInput: {
                  maxLength: 10, // Limita la entrada a 10 caracteres
                  style: { color: 'white' }, // Personaliza el estilo del input
                },
                inputLabel: {
                  style: { color: 'white' }, // Personaliza el estilo de la etiqueta
                },
              }}

              />{/**es un input etsos reciben algun valor un nombre, etc  */} 
              
              <TextField 
              variant="filled" 
              label='Clave'
              
              sx={{
                display: "block",
                margin: '.5rem 0'
              }}
              name = 'clave'
              onChange={handleChange}

              slotProps={{
                htmlInput: {
                  maxLength: 10, // Limita la entrada a 10 caracteres
                  style: { color: 'white' }, // Personaliza el estilo del input
                },
                inputLabel: {
                  style: { color: 'white' }, // Personaliza el estilo de la etiqueta
                },
              }}

              />

              <TextField  
              variant='filled' 
              label="Descripcion"
              
              multiline
              rows={4}
              sx={{
                display: "block",
                margin: '.5rem 0'
              }}

              name = 'descripcion'
              onChange={handleChange}

              slotProps={{
                htmlInput: {
                  //maxLength: 10, // Limita la entrada a 10 caracteres
                  style: { color: 'white' }, // Personaliza el estilo del input
                },
                inputLabel: {
                  style: { color: 'white' }, // Personaliza el estilo de la etiqueta
                },
              }}
              />

              <Button
              variant="contained" 
              color="primary" 
              type="submit"
              >
                Guardar
              </Button>
            </form>
          </CardContent> 
        </Card>
      </Grid2>
    </Grid2>
  )
}

export default CrearUsuario
