import { Button, Card, CardContent, Grid2, TextField, Typography } from "@mui/material"
import { useState, useEffect } from "react";

function CrearUsuario() {

  const [valores, setValores] = useState({
    usuarios: '',
    clave: '',
    descripcion: ''
  })
  const [Clave, setClave] = useState("")
  const [Descripcion, setDescripcion] = useState("")

  const handleSubmit = e =>{
    e.preventDefault();
    console.log(Usuario, Clave, Descripcion);
  }

  const handleChange = (e) => {
    const { name, value } = e.target;
  
    if (name === "usuario") {
      setUsuario(value);
    } else if (name === "clave") {
      setClave(value);
    } else if (name === "descripcion") {
      setDescripcion(value);
    }
  };

  return (
    <Grid2 container direction="colum" alignItems='center' justifyContent='center'>
      <Grid2 item xs={3}>
        <Card sx={{mt: 5}} style={{
          backgroundColor: '#1e272e',
          padding: "1rem"
        }}>
          <Typography variant="5" textAlign="center" color="white">
            Crear tarea
          </Typography>

          <CardContent> {/** par acolocar elementos dentro de la tarjeta */}
            <form onSubmit={handleSubmit}>
              <TextField  
              variant='filled' 
              label="Usuario"
              value={Usuario} // Asignamos el valor del estado Usuario
              sx={{
                display: "block",
                margin: '.5rem 0'
              }}
              name = 'usuario'
              onChange={handleChange}

              slotProps={{
                htmlInput: {
                  maxLength: 10, // Limita la entrada a 10 caracteres
                  style: { color: 'white' }, // Personaliza el estilo del input
                },
                inputLabel: {
                  style: { color: 'white' }, // Personaliza el estilo de la etiqueta
                },
              }}

              />{/**es un input etsos reciben algun valor un nombre, etc  */} 
              
              <TextField 
              variant="filled" 
              label='Clave'
              value={Clave} // Asignamos el valor del estado Clave
              sx={{
                display: "block",
                margin: '.5rem 0'
              }}
              name = 'clave'
              onChange={handleChange}

              slotProps={{
                htmlInput: {
                  maxLength: 10, // Limita la entrada a 10 caracteres
                  style: { color: 'white' }, // Personaliza el estilo del input
                },
                inputLabel: {
                  style: { color: 'white' }, // Personaliza el estilo de la etiqueta
                },
              }}

              />

              <TextField  
              variant='filled' 
              label="Descripcion"
              value={Descripcion} // Asignamos el valor del estado Descripcion
              multiline
              rows={4}
              sx={{
                display: "block",
                margin: '.5rem 0'
              }}

              name = 'descripcion'
              onChange={handleChange}

              slotProps={{
                htmlInput: {
                  //maxLength: 10, // Limita la entrada a 10 caracteres
                  style: { color: 'white' }, // Personaliza el estilo del input
                },
                inputLabel: {
                  style: { color: 'white' }, // Personaliza el estilo de la etiqueta
                },
              }}
              />

              <Button
              variant="contained" 
              color="primary" 
              type="submit"
              >
                Guardar
              </Button>
            </form>
          </CardContent> 
        </Card>
      </Grid2>
    </Grid2>
  )
}

export default CrearUsuario
